// SPDX-License-Identifier: UNLICENSED
pragma solidity =0.7.6;
pragma abicoder v2;

import "lib/openzeppelin-contracts/contracts/math/SafeMath.sol";
import "lib/openzeppelin-contracts/contracts/math/SignedSafeMath.sol";
import "lib/v3-periphery/contracts/libraries/LiquidityAmounts.sol";
import "lib/v3-core/contracts/libraries/TickMath.sol";
import "./DataType.sol";

library PositionLib {
    using SafeMath for uint256;
    using SignedSafeMath for int256;

    function emptyPosition() internal pure returns (DataType.Position memory) {
        DataType.LPT[] memory lpts = new DataType.LPT[](0);
        return DataType.Position(0, 0, 0, 0, lpts);
    }

    /**
     * @notice Calculates required token amounts to open position.
     * @param _destPosition position to open
     * @param _sqrtPrice square root price to calculate
     */
    function getRequiredTokenAmountsToOpen(DataType.Position memory _destPosition, uint160 _sqrtPrice)
        external
        pure
        returns (int256, int256)
    {
        return getRequiredTokenAmounts(emptyPosition(), _destPosition, _sqrtPrice);
    }

    /**
     * @notice Calculates required token amounts to close position.
     * @param _srcPosition position to close
     * @param _sqrtPrice square root price to calculate
     */
    function getRequiredTokenAmountsToClose(DataType.Position memory _srcPosition, uint160 _sqrtPrice)
        external
        pure
        returns (int256, int256)
    {
        return getRequiredTokenAmounts(_srcPosition, emptyPosition(), _sqrtPrice);
    }

    /**
     * @notice Calculates required token amounts to update position.
     * @param _srcPosition position to update
     * @param _destPosition desired position
     * @param _sqrtPrice square root price to calculate
     */
    function getRequiredTokenAmounts(
        DataType.Position memory _srcPosition,
        DataType.Position memory _destPosition,
        uint160 _sqrtPrice
    ) internal pure returns (int256 requiredAmount0, int256 requiredAmount1) {
        requiredAmount0 = requiredAmount0.sub(int256(_srcPosition.collateral0));
        requiredAmount1 = requiredAmount1.sub(int256(_srcPosition.collateral1));
        requiredAmount0 = requiredAmount0.add(int256(_srcPosition.debt0));
        requiredAmount1 = requiredAmount1.add(int256(_srcPosition.debt1));

        requiredAmount0 = requiredAmount0.add(int256(_destPosition.collateral0));
        requiredAmount1 = requiredAmount1.add(int256(_destPosition.collateral1));
        requiredAmount0 = requiredAmount0.sub(int256(_destPosition.debt0));
        requiredAmount1 = requiredAmount1.sub(int256(_destPosition.debt1));

        for (uint256 i = 0; i < _srcPosition.lpts.length; i++) {
            DataType.LPT memory lpt = _srcPosition.lpts[i];

            (uint256 amount0, uint256 amount1) = LiquidityAmounts.getAmountsForLiquidity(
                _sqrtPrice,
                TickMath.getSqrtRatioAtTick(lpt.lowerTick),
                TickMath.getSqrtRatioAtTick(lpt.upperTick),
                lpt.liquidity
            );

            if (lpt.isCollateral) {
                requiredAmount0 = requiredAmount0.sub(int256(amount0));
                requiredAmount1 = requiredAmount1.sub(int256(amount1));
            } else {
                requiredAmount0 = requiredAmount0.add(int256(amount0));
                requiredAmount1 = requiredAmount1.add(int256(amount1));
            }
        }

        for (uint256 i = 0; i < _destPosition.lpts.length; i++) {
            DataType.LPT memory lpt = _destPosition.lpts[i];

            (uint256 amount0, uint256 amount1) = LiquidityAmounts.getAmountsForLiquidity(
                _sqrtPrice,
                TickMath.getSqrtRatioAtTick(lpt.lowerTick),
                TickMath.getSqrtRatioAtTick(lpt.upperTick),
                lpt.liquidity
            );

            if (lpt.isCollateral) {
                requiredAmount0 = requiredAmount0.add(int256(amount0));
                requiredAmount1 = requiredAmount1.add(int256(amount1));
            } else {
                requiredAmount0 = requiredAmount0.sub(int256(amount0));
                requiredAmount1 = requiredAmount1.sub(int256(amount1));
            }
        }
    }

    function calculateLengthOfPositionUpdates(DataType.Position memory _position)
        internal
        pure
        returns (uint256 length)
    {
        length = _position.lpts.length;

        if (_position.collateral0 > 0 || _position.collateral1 > 0) {
            length += 1;
        }

        if (_position.debt0 > 0 || _position.debt1 > 0) {
            length += 1;
        }
    }

    function calculatePositionUpdatesToOpen(DataType.Position memory _position)
        external
        pure
        returns (DataType.PositionUpdate[] memory positionUpdates, uint256 swapIndex)
    {
        positionUpdates = new DataType.PositionUpdate[](calculateLengthOfPositionUpdates(_position) + 1);

        uint256 index;

        for (uint256 i = 0; i < _position.lpts.length; i++) {
            DataType.LPT memory lpt = _position.lpts[i];
            if (!lpt.isCollateral) {
                positionUpdates[index] = DataType.PositionUpdate(
                    DataType.PositionUpdateType.BORROW_LPT,
                    false,
                    lpt.liquidity,
                    lpt.lowerTick,
                    lpt.upperTick,
                    0,
                    0
                );
                index++;
            }
        }

        if (_position.collateral0 > 0 || _position.collateral1 > 0) {
            positionUpdates[index] = DataType.PositionUpdate(
                DataType.PositionUpdateType.DEPOSIT_TOKEN,
                false,
                0,
                0,
                0,
                _position.collateral0,
                _position.collateral1
            );
            index++;
        }

        if (_position.debt0 > 0 || _position.debt1 > 0) {
            positionUpdates[index] = DataType.PositionUpdate(
                DataType.PositionUpdateType.BORROW_TOKEN,
                false,
                0,
                0,
                0,
                _position.debt0,
                _position.debt1
            );
            index++;
        }

        swapIndex = index;
        index++;

        for (uint256 i; i < _position.lpts.length; i++) {
            DataType.LPT memory lpt = _position.lpts[i];
            if (lpt.isCollateral) {
                positionUpdates[index] = DataType.PositionUpdate(
                    DataType.PositionUpdateType.DEPOSIT_LPT,
                    false,
                    lpt.liquidity,
                    lpt.lowerTick,
                    lpt.upperTick,
                    0,
                    0
                );
                index++;
            }
        }
    }

    function calculatePositionUpdatesToClose(DataType.Position memory _position)
        external
        pure
        returns (DataType.PositionUpdate[] memory positionUpdates, uint256 swapIndex)
    {
        positionUpdates = new DataType.PositionUpdate[](calculateLengthOfPositionUpdates(_position) + 1);

        uint256 index = 0;

        for (uint256 i = 0; i < _position.lpts.length; i++) {
            DataType.LPT memory lpt = _position.lpts[i];
            if (lpt.isCollateral) {
                positionUpdates[index] = DataType.PositionUpdate(
                    DataType.PositionUpdateType.WITHDRAW_LPT,
                    false,
                    lpt.liquidity,
                    lpt.lowerTick,
                    lpt.upperTick,
                    0,
                    0
                );
                index++;
            }
        }

        swapIndex = index;
        index++;

        for (uint256 i = 0; i < _position.lpts.length; i++) {
            DataType.LPT memory lpt = _position.lpts[i];
            if (!lpt.isCollateral) {
                positionUpdates[index] = DataType.PositionUpdate(
                    DataType.PositionUpdateType.REPAY_LPT,
                    false,
                    lpt.liquidity,
                    lpt.lowerTick,
                    lpt.upperTick,
                    0,
                    0
                );
                index++;
            }
        }

        if (_position.collateral0 > 0 || _position.collateral1 > 0) {
            positionUpdates[index] = DataType.PositionUpdate(
                DataType.PositionUpdateType.WITHDRAW_TOKEN,
                false,
                0,
                0,
                0,
                (_position.collateral0 * 12) / 10,
                (_position.collateral1 * 12) / 10
            );
            index++;
        }

        if (_position.debt0 > 0 || _position.debt1 > 0) {
            positionUpdates[index] = DataType.PositionUpdate(
                DataType.PositionUpdateType.REPAY_TOKEN,
                false,
                0,
                0,
                0,
                (_position.debt0 * 12) / 10,
                (_position.debt1 * 12) / 10
            );
        }
    }
}

src/libraries/InterestCalculator.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity =0.7.6;
pragma abicoder v2;

import "lib/openzeppelin-contracts/contracts/math/SafeMath.sol";
import "lib/openzeppelin-contracts/contracts/utils/SafeCast.sol";
import "lib/v3-core/contracts/interfaces/IUniswapV3Pool.sol";
import "lib/v3-core/contracts/libraries/TickMath.sol";
import "lib/v3-periphery/contracts/interfaces/INonfungiblePositionManager.sol";
import "lib/v3-periphery/contracts/libraries/LiquidityAmounts.sol";

import "./DataType.sol";
import "./BaseToken.sol";
import "./LPTMath.sol";

library InterestCalculator {
    using SafeMath for uint256;
    using SafeCast for uint256;
    using BaseToken for BaseToken.TokenState;

    uint256 internal constant ONE = 1e18;

    uint256 internal constant Q96 = 0x1000000000000000000000000;

    struct TickSnapshot {
        uint256 lastFeeGrowthInside0X128;
        uint256 lastFeeGrowthInside1X128;
        uint256 lastFeeGrowthGlobal0X128;
        uint256 lastFeeGrowthGlobal1X128;
    }

    struct TickInfo {
        uint256 feeGrowthOutside0X128;
        uint256 feeGrowthOutside1X128;
    }

    struct DPMParams {
        IRMParams premiumParams;
        IRMParams irmParams;
        mapping(bytes32 => TickSnapshot) snapshots;
    }

    struct IRMParams {
        uint256 baseRate;
        uint256 kinkRate;
        uint256 slope1;
        uint256 slope2;
    }

    function applyDailyPremium(
        DPMParams storage _params,
        DataType.Context memory _context,
        DataType.PerpStatus storage _perpState,
        uint160 _sqrtPrice
    ) internal {
        if (block.timestamp <= _perpState.lastTouchedTimestamp) {
            return;
        }

        if (_perpState.borrowedLiquidity > 0) {
            uint256 premium = ((block.timestamp - _perpState.lastTouchedTimestamp) *
                calculateYearlyPremium(_params, _context, _perpState, _sqrtPrice)) / 365 days;

            _perpState.premiumGrowthForBorrower = _perpState.premiumGrowthForBorrower.add(premium);
            _perpState.premiumGrowthForLender = _perpState.premiumGrowthForLender.add(
                PredyMath.mulDiv(premium, _perpState.borrowedLiquidity, getTotalLiquidityAmount(_context, _perpState))
            );
        }

        takeSnapshotForRange(_params, IUniswapV3Pool(_context.uniswapPool), _perpState.lowerTick, _perpState.upperTick);

        _perpState.lastTouchedTimestamp = block.timestamp;
    }

    function calculateYearlyPremium(
        DPMParams storage _params,
        DataType.Context memory _context,
        DataType.PerpStatus storage _perpState,
        uint160 _sqrtPrice
    ) internal view returns (uint256) {
        if (_perpState.borrowedLiquidity > 0) {
            uint256 perpUr = getPerpUR(_context, _perpState);

            uint256 dailyPremium = calculateStableValueFromTotalPremiumValue(
                calculatePremium(
                    _params,
                    IUniswapV3Pool(_context.uniswapPool),
                    _perpState.lowerTick,
                    _perpState.upperTick,
                    perpUr
                ),
                getAvailableLiquidityAmount(_context, _perpState)
            );

            uint256 dailyInterest = calculateStableValueFromRatio(
                _context.isMarginZero,
                calculateInterestRate(_params.irmParams, perpUr),
                _sqrtPrice,
                _perpState.lowerTick,
                _perpState.upperTick
            );

            return dailyPremium + dailyInterest;
        }

        return 0;
    }

    function applyInterest(
        DataType.Context storage _context,
        IRMParams memory _irmParams,
        uint256 lastTouchedTimestamp
    ) internal returns (uint256) {
        if (block.timestamp <= lastTouchedTimestamp) {
            return lastTouchedTimestamp;
        }

        // calculate interest for tokens
        uint256 interest = ((block.timestamp - lastTouchedTimestamp) *
            calculateInterestRate(_irmParams, getUR(_context))) / 365 days;

        _context.tokenState0.updateScaler(interest);
        _context.tokenState1.updateScaler(interest);

        return block.timestamp;
    }

    function calculateStableValueFromTotalPremiumValue(uint256 _premiumInUsd, uint256 _totalLiquidity)
        internal
        pure
        returns (uint256 value)
    {
        return (_premiumInUsd * 1e18) / _totalLiquidity;
    }

    function calculateStableValueFromRatio(
        bool _isMarginZero,
        uint256 _ratio,
        uint160 _sqrtPrice,
        int24 _lowerTick,
        int24 _upperTick
    ) internal pure returns (uint256 value) {
        (uint256 amount0, uint256 amount1) = LiquidityAmounts.getAmountsForLiquidity(
            _sqrtPrice,
            TickMath.getSqrtRatioAtTick(_lowerTick),
            TickMath.getSqrtRatioAtTick(_upperTick),
            1e18
        );

        uint256 price = LPTMath.decodeSqrtPriceX96(_isMarginZero, _sqrtPrice);

        if (_isMarginZero) {
            value = PredyMath.mulDiv(amount1, price, 1e18) + amount0;
        } else {
            value = PredyMath.mulDiv(amount0, price, 1e18) + amount1;
        }

        // value (usd/liquidity)
        value = (value * _ratio) / 1e18;
    }

    function getAvailableLiquidityAmount(DataType.Context memory _context, DataType.PerpStatus memory _perpState)
        internal
        view
        returns (uint256)
    {
        (, , , , , , , uint128 liquidity, , , , ) = INonfungiblePositionManager(_context.positionManager).positions(
            _perpState.tokenId
        );

        return liquidity;
    }

    function getTotalLiquidityAmount(DataType.Context memory _context, DataType.PerpStatus memory _perpState)
        internal
        view
        returns (uint256)
    {
        return getAvailableLiquidityAmount(_context, _perpState) + _perpState.borrowedLiquidity;
    }

    function calculatePremium(
        DPMParams storage _params,
        IUniswapV3Pool uniPool,
        int24 _lowerTick,
        int24 _upperTick,
        uint256 _utilizationRatio
    ) internal view returns (uint256) {
        uint256 feeGrowthGlobal0X128 = uniPool.feeGrowthGlobal0X128();
        uint256 feeGrowthGlobal1X128 = uniPool.feeGrowthGlobal1X128();

        (, int24 tickCurrent, , , , , ) = uniPool.slot0();

        (uint256 feeGrowthInside0X128, uint256 feeGrowthInside1X128) = getFeeGrowthInside(
            getFeeGrowthOutside(uniPool, _lowerTick),
            getFeeGrowthOutside(uniPool, _upperTick),
            _lowerTick,
            _upperTick,
            tickCurrent,
            feeGrowthGlobal0X128,
            feeGrowthGlobal1X128
        );

        bytes32 key = keccak256(abi.encodePacked(_lowerTick, _upperTick));

        return
            calculateRatio(
                calculateInterestRate(_params.premiumParams, _utilizationRatio),
                _params.snapshots[key],
                feeGrowthInside0X128,
                feeGrowthInside1X128,
                feeGrowthGlobal0X128,
                feeGrowthGlobal1X128
            );
    }

    function calculateRatio(
        uint256 _dailyFeeAmount,
        TickSnapshot memory snapshot,
        uint256 feeGrowthInside0X128,
        uint256 feeGrowthInside1X128,
        uint256 feeGrowthGlobal0X128,
        uint256 feeGrowthGlobal1X128
    ) internal pure returns (uint256) {
        uint256 a;
        uint256 b;

        if (feeGrowthGlobal0X128 > snapshot.lastFeeGrowthGlobal0X128) {
            a =
                (ONE * (feeGrowthInside0X128 - snapshot.lastFeeGrowthInside0X128)) /
                (feeGrowthGlobal0X128 - snapshot.lastFeeGrowthGlobal0X128);
        }

        if (feeGrowthGlobal1X128 > snapshot.lastFeeGrowthGlobal1X128) {
            b =
                (ONE * (feeGrowthInside1X128 - snapshot.lastFeeGrowthInside1X128)) /
                (feeGrowthGlobal1X128 - snapshot.lastFeeGrowthGlobal1X128);
        }

        return (_dailyFeeAmount * (a + b)) / (2 * ONE);
    }

    function getPerpUR(DataType.Context memory _context, DataType.PerpStatus storage _perpState)
        internal
        view
        returns (uint256)
    {
        return PredyMath.mulDiv(_perpState.borrowedLiquidity, ONE, getTotalLiquidityAmount(_context, _perpState));
    }

    function getUR(DataType.Context memory _context) internal pure returns (uint256) {
        if (_context.tokenState0.totalDeposited == 0) {
            return ONE;
        }
        return PredyMath.mulDiv(_context.tokenState0.totalBorrowed, ONE, _context.tokenState0.totalDeposited);
    }

    function calculateInterestRate(IRMParams memory _irmParams, uint256 _utilizationRatio)
        internal
        pure
        returns (uint256)
    {
        uint256 ir = _irmParams.baseRate;

        if (_utilizationRatio <= _irmParams.kinkRate) {
            ir += (_utilizationRatio * _irmParams.slope1) / ONE;
        } else {
            ir += (_irmParams.kinkRate * _irmParams.slope1) / ONE;
            ir += (_irmParams.slope2 * (_utilizationRatio - _irmParams.kinkRate)) / ONE;
        }

        return ir;
    }

    function takeSnapshotForRange(
        DPMParams storage _params,
        IUniswapV3Pool uniPool,
        int24 _lowerTick,
        int24 _upperTick
    ) internal {
        uint256 feeGrowthGlobal0X128 = uniPool.feeGrowthGlobal0X128();
        uint256 feeGrowthGlobal1X128 = uniPool.feeGrowthGlobal1X128();

        (, int24 tickCurrent, , , , , ) = uniPool.slot0();

        (uint256 feeGrowthInside0X128, uint256 feeGrowthInside1X128) = getFeeGrowthInside(
            getFeeGrowthOutside(uniPool, _lowerTick),
            getFeeGrowthOutside(uniPool, _upperTick),
            _lowerTick,
            _upperTick,
            tickCurrent,
            feeGrowthGlobal0X128,
            feeGrowthGlobal1X128
        );

        bytes32 key = keccak256(abi.encodePacked(_lowerTick, _upperTick));

        _params.snapshots[key].lastFeeGrowthInside0X128 = feeGrowthInside0X128;
        _params.snapshots[key].lastFeeGrowthInside1X128 = feeGrowthInside1X128;
        _params.snapshots[key].lastFeeGrowthGlobal0X128 = feeGrowthGlobal0X128;
        _params.snapshots[key].lastFeeGrowthGlobal1X128 = feeGrowthGlobal1X128;
    }

    function getFeeGrowthOutside(IUniswapV3Pool uniPool, int24 _tickId) internal view returns (TickInfo memory) {
        (, , uint256 feeGrowthOutside0X128, uint256 feeGrowthOutside1X128, , , , bool initialized) = uniPool.ticks(
            _tickId
        );

        require(initialized, "initialized");

        return TickInfo(feeGrowthOutside0X128, feeGrowthOutside1X128);
    }

    function getFeeGrowthInside(
        TickInfo memory lower,
        TickInfo memory upper,
        int24 tickLower,
        int24 tickUpper,
        int24 tickCurrent,
        uint256 feeGrowthGlobal0X128,
        uint256 feeGrowthGlobal1X128
    ) internal pure returns (uint256 feeGrowthInside0X128, uint256 feeGrowthInside1X128) {
        // calculate fee growth below
        uint256 feeGrowthBelow0X128;
        uint256 feeGrowthBelow1X128;

        if (tickCurrent >= tickLower) {
            // 1808790600867964665724044468235309
            feeGrowthBelow0X128 = lower.feeGrowthOutside0X128;
            feeGrowthBelow1X128 = lower.feeGrowthOutside1X128;
        } else {
            feeGrowthBelow0X128 = feeGrowthGlobal0X128 - lower.feeGrowthOutside0X128;
            feeGrowthBelow1X128 = feeGrowthGlobal1X128 - lower.feeGrowthOutside1X128;
        }

        // calculate fee growth above
        uint256 feeGrowthAbove0X128;
        uint256 feeGrowthAbove1X128;
        if (tickCurrent < tickUpper) {
            //  1808790600867964665724044468235309
            feeGrowthAbove0X128 = upper.feeGrowthOutside0X128;
            feeGrowthAbove1X128 = upper.feeGrowthOutside1X128;
        } else {
            feeGrowthAbove0X128 = feeGrowthGlobal0X128 - upper.feeGrowthOutside0X128;
            feeGrowthAbove1X128 = feeGrowthGlobal1X128 - upper.feeGrowthOutside1X128;
        }

        feeGrowthInside0X128 = feeGrowthGlobal0X128 - feeGrowthBelow0X128 - feeGrowthAbove0X128;
        feeGrowthInside1X128 = feeGrowthGlobal1X128 - feeGrowthBelow1X128 - feeGrowthAbove1X128;
    }
}

src/Controller.sol.content:
//SPDX-License-Identifier: agpl-3.0
pragma solidity ^0.7.6;
pragma abicoder v2;

import {Initializable} from "lib/openzeppelin-contracts/contracts/proxy/Initializable.sol";
import "lib/openzeppelin-contracts/contracts/token/ERC20/ERC20.sol";
import "lib/openzeppelin-contracts/contracts/math/SafeMath.sol";
import "lib/openzeppelin-contracts/contracts/math/SignedSafeMath.sol";
import "lib/openzeppelin-contracts/contracts/utils/SafeCast.sol";
import "lib/v3-periphery/contracts/interfaces/INonfungiblePositionManager.sol";
import {TransferHelper} from "lib/v3-periphery/contracts/libraries/TransferHelper.sol";
import "lib/v3-core/contracts/interfaces/IUniswapV3Pool.sol";
import "lib/v3-core/contracts/interfaces/IUniswapV3Factory.sol";
import "lib/v3-core/contracts/libraries/FixedPoint128.sol";
import "lib/v3-periphery/contracts/interfaces/ISwapRouter.sol";

import "./interfaces/IController.sol";
import {BaseToken} from "./libraries/BaseToken.sol";
import "./libraries/DataType.sol";
import "./libraries/VaultLib.sol";
import "./libraries/PredyMath.sol";
import "./libraries/PositionUpdater.sol";
import "./libraries/PositionCalculator.sol";
import "./libraries/InterestCalculator.sol";
import "./Constants.sol";

import "lib/forge-std/src/console.sol";

/**
 * Error Codes
 * P1: caller must be vault owner
 * P2: caller must be vault owner
 * P3: must not be liquidatable
 * P4: must be liquidatable
 * P5: no enough token0
 * P6: no enough token1
 * P7: debt must be 0
 */
contract Controller is IController, Constants, Initializable {
    using BaseToken for BaseToken.TokenState;
    using SafeMath for uint256;
    using SafeMath for uint128;
    using SignedSafeMath for int256;
    using SafeCast for uint256;
    using SafeCast for int256;
    using VaultLib for DataType.Vault;

    uint256 public oraclePeriod;

    uint256 public lastTouchedTimestamp;

    mapping(bytes32 => DataType.PerpStatus) private ranges;

    uint256 public vaultIdCount;

    mapping(uint256 => DataType.Vault) public vaults;

    DataType.Context public context;
    InterestCalculator.IRMParams private irmParams;
    InterestCalculator.DPMParams private dpmParams;

    address public operator;

    event VaultCreated(uint256 vaultId, address owner);
    event PositionUpdated(uint256 vaultId, int256 a0, int256 a1, uint160 sqrtPrice, bytes metadata);

    modifier onlyOperator() {
        require(operator == msg.sender, "caller must be operator");
        _;
    }

    modifier onlyVaultOwner(uint256 _vaultId) {
        require(vaults[_vaultId].owner == msg.sender, "P1");
        _;
    }

    constructor() {
    }

    function initialize(
        DataType.InitializationParams memory _initializationParams,
        address _positionManager,
        address _factory,
        address _swapRouter
    ) public initializer {
        context.feeTier = _initializationParams.feeTier;
        context.token0 = _initializationParams.token0;
        context.token1 = _initializationParams.token1;
        context.isMarginZero = _initializationParams.isMarginZero;
        context.positionManager = _positionManager;
        context.swapRouter = _swapRouter;

        PoolAddress.PoolKey memory poolKey = PoolAddress.PoolKey({
            token0: context.token0,
            token1: context.token1,
            fee: context.feeTier
        });

        context.uniswapPool = PoolAddress.computeAddress(_factory, poolKey);

        vaultIdCount = 1;

        ERC20(context.token0).approve(address(_positionManager), type(uint256).max);
        ERC20(context.token1).approve(address(_positionManager), type(uint256).max);
        ERC20(context.token0).approve(address(_swapRouter), type(uint256).max);
        ERC20(context.token1).approve(address(_swapRouter), type(uint256).max);

        context.tokenState0.initialize();
        context.tokenState1.initialize();

        lastTouchedTimestamp = block.timestamp;

        operator = msg.sender;

        oraclePeriod = 1 minutes;
    }

    function setOperator(address _newOperator) external onlyOperator {
        operator = _newOperator;
    }

    function setOperator(uint256 _oraclePeriod) external onlyOperator {
        oraclePeriod = _oraclePeriod;
    }

    function updateIRMParams(InterestCalculator.IRMParams memory _irmParams) external onlyOperator {
        irmParams = _irmParams;
    }

    function updateDRMParams(
        InterestCalculator.IRMParams memory _irmParams,
        InterestCalculator.IRMParams memory _premiumParams
    ) external onlyOperator {
        dpmParams.irmParams = _irmParams;
        dpmParams.premiumParams = _premiumParams;
    }

    // User API
    /**
     * @notice Update position in a vault.
     * @param _vaultId vault id
     * @param _positionUpdates parameters to update position
     * @param _buffer0 max amount of token0 to send
     * @param _buffer1 max amount of token1 to send
     */
    function updatePosition(
        uint256 _vaultId,
        DataType.PositionUpdate[] memory _positionUpdates,
        uint256 _buffer0,
        uint256 _buffer1,
        DataType.TradeOption memory _tradeOption,
        bytes memory _metadata
    ) public override returns (uint256 vaultId) {
        applyPerpFee(_vaultId);

        DataType.Vault storage vault;
        (vaultId, vault) = createOrGetVault(_vaultId, _tradeOption.quoterMode);

        if (_buffer0 > 0) {
            TransferHelper.safeTransferFrom(context.token0, msg.sender, address(this), _buffer0);
        }
        if (_buffer1 > 0) {
            TransferHelper.safeTransferFrom(context.token1, msg.sender, address(this), _buffer1);
        }

        // update position
        (int256 requiredAmount0, int256 requiredAmount1) = PositionUpdater.updatePosition(
            vault,
            context,
            ranges,
            _positionUpdates,
            _tradeOption
        );

        if (_tradeOption.quoterMode) {
            revertRequiredAmounts(requiredAmount0, requiredAmount1);
        }

        require(!_checkLiquidatable(vaultId), "P3");

        require(int256(_buffer0) >= requiredAmount0, "P5");
        require(int256(_buffer1) >= requiredAmount1, "P6");

        if (int256(_buffer0) > requiredAmount0) {
            TransferHelper.safeTransfer(context.token0, msg.sender, uint256(int256(_buffer0).sub(requiredAmount0)));
        }
        if (int256(_buffer1) > requiredAmount1) {
            TransferHelper.safeTransfer(context.token1, msg.sender, uint256(int256(_buffer1).sub(requiredAmount1)));
        }

        emit PositionUpdated(vaultId, requiredAmount0, requiredAmount1, getSqrtPrice(), _metadata);
    }

    function _reducePosition(
        uint256 _vaultId,
        DataType.PositionUpdate[] memory _positionUpdates,
        uint256 _penaltyAmount,
        bool _swapAnyway
    ) internal returns (uint256 penaltyAmount) {
        applyPerpFee(_vaultId);

        DataType.Vault storage vault = vaults[_vaultId];

        // reduce debt
        (int256 surplusAmount0, int256 surplusAmount1) = PositionUpdater.updatePosition(
            vault,
            context,
            ranges,
            _positionUpdates,
            // reduce only
            DataType.TradeOption(true, _swapAnyway, false, context.isMarginZero)
        );
        surplusAmount0 = -surplusAmount0;
        surplusAmount1 = -surplusAmount1;

        require(0 <= surplusAmount0, "P5");
        require(0 <= surplusAmount1, "P6");

        if (context.isMarginZero) {
            (surplusAmount0, penaltyAmount) = PredyMath.subReward(surplusAmount0, _penaltyAmount);
        } else {
            (surplusAmount1, penaltyAmount) = PredyMath.subReward(surplusAmount1, _penaltyAmount);
        }

        if (0 < surplusAmount0) {
            context.tokenState0.addCollateral(vault.balance0, uint256(surplusAmount0), false);
        }
        if (0 < surplusAmount1) {
            context.tokenState1.addCollateral(vault.balance1, uint256(surplusAmount1), false);
        }
    }

    /**
     * @notice Anyone can liquidates the vault if its required collateral value is positive.
     * @param _vaultId vault id
     * @param _positionUpdates parameters to update position
     */
    function liquidate(
        uint256 _vaultId,
        DataType.PositionUpdate[] memory _positionUpdates,
        bool _swapAnyway
    ) public override {
        applyPerpFee(_vaultId);

        // check liquidation
        require(_checkLiquidatable(_vaultId), "P4");

        (uint160 sqrtPrice, ) = LPTMath.callUniswapObserve(IUniswapV3Pool(context.uniswapPool), oraclePeriod);

        // calculate penalty
        uint256 debtValue = vaults[_vaultId].getDebtPositionValue(ranges, context, sqrtPrice);

        // close position
        uint256 penaltyAmount = _reducePosition(_vaultId, _positionUpdates, debtValue / 200, _swapAnyway);

        require(vaults[_vaultId].getDebtPositionValue(ranges, context, sqrtPrice) == 0, "P7");

        sendReward(msg.sender, penaltyAmount);
    }

    /**
     * @notice Contract owner can close positions
     * @param _data vaults data to close position
     */
    function forceClose(bytes[] memory _data) external onlyOperator {
        for (uint256 i = 0; i < _data.length; i++) {
            (uint256 vaultId, DataType.PositionUpdate[] memory _positionUpdates) = abi.decode(
                _data[i],
                (uint256, DataType.PositionUpdate[])
            );

            applyPerpFee(vaultId);

            _reducePosition(vaultId, _positionUpdates, 0, false);
        }
    }

    // Getter Functions

    function getIsMarginZero() external view returns (bool) {
        return context.isMarginZero;
    }

    function getRange(bytes32 _rangeId) external view returns (DataType.PerpStatus memory) {
        return ranges[_rangeId];
    }

    /**
     * @notice Returns the flag whether a vault can be liquidated or not.
     * @param _vaultId vault id
     */
    function checkLiquidatable(uint256 _vaultId) external returns (bool) {
        applyPerpFee(_vaultId);

        return _checkLiquidatable(_vaultId);
    }

    /**
     * @notice Returns collateral value and debt value.
     * @param _vaultId vault id
     */
    function getVaultStatus(uint256 _vaultId) external returns (DataType.VaultStatus memory) {
        uint160 sqrtPriceX96 = getSqrtPrice();

        applyPerpFee(_vaultId);

        return vaults[_vaultId].getVaultStatus(ranges, context, sqrtPriceX96);
    }

    function getVault(uint256 _vaultId) external view returns (DataType.Vault memory) {
        return vaults[_vaultId];
    }

    function calculateYearlyPremium(bytes32 _rangeId) external view returns (uint256) {
        return InterestCalculator.calculateYearlyPremium(dpmParams, context, ranges[_rangeId], getSqrtPrice());
    }

    // Private Functions

    function _checkLiquidatable(uint256 _vaultId) internal view returns (bool) {
        (uint160 sqrtPrice, ) = LPTMath.callUniswapObserve(IUniswapV3Pool(context.uniswapPool), oraclePeriod);

        // calculate value using TWAP price
        int256 requiredCollateral = PositionCalculator.calculateRequiredCollateral(
            _getPosition(_vaultId),
            sqrtPrice,
            context.isMarginZero
        );

        return requiredCollateral > 0;
    }

    function createOrGetVault(uint256 _vaultId, bool _quoterMode)
        internal
        returns (uint256 vaultId, DataType.Vault storage)
    {
        if (_vaultId == 0) {
            vaultId = vaultIdCount;
            vaultIdCount++;

            vaults[vaultId].vaultId = vaultId;
            vaults[vaultId].owner = msg.sender;

            emit VaultCreated(vaultId, vaults[vaultId].owner);
        } else {
            vaultId = _vaultId;
            require(vaults[vaultId].owner == msg.sender || _quoterMode, "P2");
        }

        return (vaultId, vaults[vaultId]);
    }

    function sendReward(address _liquidator, uint256 _reward) internal {
        TransferHelper.safeTransfer(context.isMarginZero ? context.token0 : context.token1, _liquidator, _reward);
    }

    function sendReward(
        address _liquidator,
        uint256 _reward0,
        uint256 _reward1
    ) internal {
        TransferHelper.safeTransfer(context.token0, _liquidator, _reward0);
        TransferHelper.safeTransfer(context.token1, _liquidator, _reward1);
    }

    function applyPerpFee(uint256 _vaultId) internal {
        DataType.Vault storage vault = vaults[_vaultId];

        // calculate fee for perps
        for (uint256 i = 0; i < vault.lpts.length; i++) {
            InterestCalculator.applyDailyPremium(dpmParams, context, ranges[vault.lpts[i].rangeId], getSqrtPrice());
        }

        lastTouchedTimestamp = InterestCalculator.applyInterest(context, irmParams, lastTouchedTimestamp);

        PositionUpdater.collectFee(context, vault, ranges);
    }

    /**
     * Gets current price of underlying token by margin token.
     */
    function getPrice() public view returns (uint256) {
        return LPTMath.decodeSqrtPriceX96(context.isMarginZero, getSqrtPrice());
    }

    function getSqrtPrice() public view returns (uint160 sqrtPriceX96) {
        (sqrtPriceX96, , , , , , ) = IUniswapV3Pool(context.uniswapPool).slot0();
    }

    function getCurrentTick() external view returns (int24 tick) {
        (, tick, , , , , ) = IUniswapV3Pool(context.uniswapPool).slot0();
    }

    /**
     * Gets Time Weighted Average Price of underlying token by margin token.
     */
    function getTWAP() external view returns (uint256) {
        return LPTMath.decodeSqrtPriceX96(context.isMarginZero, getTWAPSqrtPrice());
    }

    function getTWAPSqrtPrice() public view returns (uint160 sqrtPriceX96) {
        (sqrtPriceX96, ) = LPTMath.callUniswapObserve(IUniswapV3Pool(context.uniswapPool), oraclePeriod);
    }

    function getPosition(uint256 _vaultId) public returns (DataType.Position memory position) {
        applyPerpFee(_vaultId);

        return _getPosition(_vaultId);
    }

    function _getPosition(uint256 _vaultId) internal view returns (DataType.Position memory position) {
        DataType.Vault memory vault = vaults[_vaultId];

        return vault.getPosition(ranges, context);
    }

    function revertRequiredAmounts(int256 _requiredAmount0, int256 _requiredAmount1) internal pure {
        assembly {
            let ptr := mload(0x20)
            mstore(ptr, _requiredAmount0)
            mstore(add(ptr, 0x20), _requiredAmount1)
            revert(ptr, 64)
        }
    }
}

